import React from "react";
// import { Form, Input, TextArea, Button } from 'semantic-ui-react';
import Button from 'react-bootstrap/Button';

import Form from 'react-bootstrap/Form';
import emailjs from 'emailjs-com';
import Swal from 'sweetalert2';
import { FormControl, FormGroup, FormLabel } from "react-bootstrap";

const SERVICE_ID = "service_uyb86zr";
const TEMPLATE_ID = "service_uyb86zr";
const USER_ID = "p-dBaZrqlV17MLUDT";

function Contact() {
    const handleOnSubmit = (e) => {
        e.preventDefault();
        emailjs.sendForm(SERVICE_ID, TEMPLATE_ID, e.target, USER_ID)
          .then((result) => {
            console.log(result.text);
            Swal.fire({
              icon: 'success',
              title: 'Message Sent Successfully'
            })
          }, (error) => {
            console.log(error.text);
            Swal.fire({
              icon: 'error',
              title: 'Ooops, something went wrong',
              text: error.text,
            })
          });
        e.target.reset()
      };

    return (
        <div className="contact-form">
            <h4>testing header</h4>

            <Form onSubmit={handleOnSubmit}>
                <FormGroup>
                    <FormLabel>Enter your name here:</FormLabel>
                    <FormControl type="text" placeholder="Enter Full Name Here"></FormControl>
                </FormGroup>

                <FormGroup>
                    <FormLabel>Enter your email here:</FormLabel>
                    <FormControl type="email" placeholder="Enter Email Here"></FormControl>
                </FormGroup>

                <FormGroup>
                    <FormLabel>Message:</FormLabel>
                    <FormControl type="email" placeholder="Tell me anything!"></FormControl>
                </FormGroup>

                <Button type='submit' color='green'>Submit</Button>
            </Form>
        </div>
    )
}

export default Contact;
